pragma solidity 0.4.25;

import "./ACL.sol";

/// @title SurveyDB Interface
contract SurveyDBInterface {
    function getAllSurveys() public pure returns (bytes32[]);

    function addSurvey(bytes32 name, bytes32[] options, uint endDate, uint256 rewardPool) public;

    function addSurveyOption(bytes32 surveyName, bytes32 surveyOption) public;

    function addStakingHistory(bytes32 surveyName, address sender, uint payoutType, uint date) public;

    function updateSurveyEndDate(bytes32 surveyName, uint endDate) public;

    function updateSurveyRewardPool(bytes32 surveyName, uint256 rewardPool) public;

    function saveAnswer(bytes32 surveyName, 
                        bytes32 participantOption, 
                        uint256 ahtVote,
                        bool typeVote,
                        uint typeReward, 
                        address participantAddress,
                        uint dateVoted) public;

    function sendStake(bytes32 surveyName, uint256 amountStaked, address sender, uint stakeStartDate) public;

    function stopStake(bytes32 surveyName, address sender, uint dateStop) public;

    function sendReward(bytes32 surveyName, address participant, uint256 reward, uint datePayout) 
        public;

    function returnAmountStake(address sender) public;

    function getSurvey(bytes32 surveyName) public pure 
        returns (bytes32[] options, bool exists, uint256 rewardPool, uint endDate, address owner);

    function getSurveyRewardPool(bytes32 surveyName) public pure returns (uint256 rewardPool);

    function getSurveyStakingHistory(bytes32 surveyName) public pure
        returns (address[] sender, uint[] payoutType, uint[] time);

    function getTotalAmountStacked() public pure returns (uint256 amountStaked);

    function getAmountStaked(address sender) public pure returns (uint256 amountStaked);

    function getStakingInfo(bytes32 surveyName, address sender) public pure 
        returns (bool stakingActive, uint stakingActiveDate, uint256 amountStaked, uint256 rewards);

    function getVoteInfo(bytes32 surveyName, address sender) public pure returns (bool voted, uint dateVoted);

    function getVotedOptions(bytes32 surveyName, address sender) public pure returns (bytes32[] votedOptions);

    function getSurveyResult(bytes32 surveyName) public pure returns (bytes32[], uint256[], uint256[]);

    function getRewardInfo(bytes32 surveyName) public pure returns (address[], uint[], uint256[], bool[], bool[]);

    function isPayable(bytes32 surveyName, address participant, uint date) public pure returns (bool withdraw);

    function getParticipantsPendingPayment(bytes32 surveyName, uint date) public pure returns (address[], bool[]);
}

contract AHTTokenInterface {
    function balanceOf(address _owner) public pure returns (uint256);

    function allowance(address _owner, address _spender) public pure returns (uint256);

    function transferFrom(address _from, address _to, uint256 _value) public pure returns (bool success);

    function transfer(address _to, uint256 _value) public pure returns (bool success);
}

contract Survey is ACL {
    SurveyDBInterface private surveyDB;
    AHTTokenInterface private ahtToken;

    event SurveyPost(
        bytes32 name,
        bytes32[] options,
        uint endDate,
        uint256 rewardPool
    );

    event SurveyOptionPost(
        bytes32 surveyName,
        bytes32 surveyOption
    );

    event SurveyStakingHistoryPost(
        bytes32 surveyName,
        address sender,
        uint payoutType, 
        uint date
    );

    event SurveyUpdateEndDatePost(
        bytes32 surveyName,
        uint endDate
    );

    event SurveyUpdateRewardPool(
        bytes32 surveyName,
        uint256 rewardPool
    );

    event SurveyAnswerPost(
        bytes32 surveyName,
        bytes32 participantOption,
        uint256 ahtVote,
        bool typeVote,
        uint typeReward,
        address participantAddress
    );

    event StakingPost(
        uint256 amountStaked,
        address sender
    );

    event StakingStopPost(
        bytes32 surveyName,
        address sender,
        uint dateStop
    );

    event SendReward(
        bytes32 surveyName,
        address participant,
        uint256 reward,
        uint datePayout,
        address payer
    );

    event ReturnAmountStaked(
        address participant,
        address payer
    );

    event TransferAHTVote(
        address participantAddress,
        uint256 ahtVote
    );

    constructor(address _surveyDB, address _ahtToken) public {
        admins[msg.sender] = true;
        levels[msg.sender] = 0;

        surveyDB = SurveyDBInterface(_surveyDB);
        ahtToken = AHTTokenInterface(_ahtToken);
    }

    function setSurveyDBContract(address _surveyDB) external onlyAdmin(0) {
        surveyDB = SurveyDBInterface(_surveyDB);
    }

    function setAHTToken(address _ahtToken) external onlyAdmin(0) {
        ahtToken = AHTTokenInterface(_ahtToken);
    }

    /**
    * @notice Function to return all the surveys
    */
    function getAllSurveys () external view returns (bytes32[] names) {
        return surveyDB.getAllSurveys();
    }

    /**
    * @notice Function to create new survey
    * @param name Survey name
    * @param options Options survey
    * @param endDate Survey end date
    * @param rewardPool Survey reward pool
    */
    function addSurvey(bytes32 name, bytes32[] options, uint endDate, uint256 rewardPool)
        external onlyAdmin(1) {
        surveyDB.addSurvey(name, options, endDate, rewardPool);

        emit SurveyPost(name, options, endDate, rewardPool);
    }

    /**
    * @notice Function to add new option to an existing survey
    * @param surveyName Survey name
    * @param surveyOption New survey option
    */
    function addSurveyOption(bytes32 surveyName, bytes32 surveyOption)
        external onlyAdmin(1) {
        surveyDB.addSurveyOption(surveyName, surveyOption);

        emit SurveyOptionPost(surveyName, surveyOption);   
    }

    /**
    * @notice Function to add a staking history row
    * @param surveyName Survey name
    * @param sender Sender address
    * @param payoutType Payout type
    * @param date Payout date
    */
    function addStakingHistory(bytes32 surveyName, address sender, uint payoutType, uint date) public {
        surveyDB.addStakingHistory(surveyName, sender, payoutType, date);

        emit SurveyStakingHistoryPost(surveyName, sender, payoutType, date);
    }

    /**
    * @notice Function to update the survey end date
    * @param surveyName Survey name
    * @param endDate Survey end date
    */
    function updateSurveyEndDate(bytes32 surveyName, uint endDate) 
        external onlyAdmin(1) {
        surveyDB.updateSurveyEndDate(surveyName, endDate);

        emit SurveyUpdateEndDatePost(surveyName, endDate); 
    }

    /**
    * @notice Function to update the survey reward pool
    * @param surveyName Survey name
    * @param rewardPool Survey reward pool
    */
    function updateSurveyRewardPool(bytes32 surveyName, uint256 rewardPool) 
        external onlyAdmin(1) {
        surveyDB.updateSurveyRewardPool(surveyName, rewardPool);

        emit SurveyUpdateRewardPool(surveyName, rewardPool);
    }

    /**
    * @notice Get user balance
    * @return balance User balance
    */
    function getBalance() external view
        returns (uint256 balance) {
        return ahtToken.balanceOf(msg.sender);
    }

    /**
    * @notice Save survey answer and transfer aht votes
    * @param surveyName Survey name
    * @param participantOption Survey option
    * @param typeVote Type of vote, true 'FOR' and false 'AGAINST'
    * @param typeReward Type reward for the participant
    * @param dateVoted Date voted
    */
    function saveAnswer(bytes32 surveyName, 
                        bytes32 participantOption, 
                        bool typeVote,
                        uint typeReward,
                        uint dateVoted) public {
        require(ahtToken.balanceOf(msg.sender) > 1.0, "The participant has not enought AHT");

        uint256 amountStaked = surveyDB.getAmountStaked(msg.sender);

        require(amountStaked >= 0, "Insufficient AHT. You need to stake.");

        surveyDB.saveAnswer(surveyName, participantOption, amountStaked, typeVote, typeReward, msg.sender, dateVoted);

        emit SurveyAnswerPost(surveyName, participantOption, amountStaked, typeVote, typeReward, msg.sender);
    }

    /**
    * @notice Send stake
    * @param surveyName Survey name
    * @param amountStaked Amount staked
    * @param stakeStartDate Stake start date
    */
    function sendStake(bytes32 surveyName, uint256 amountStaked, uint stakeStartDate) public {
        require(ahtToken.balanceOf(msg.sender) > 1.0, "The participant has not enought AHT");
        require(ahtToken.balanceOf(msg.sender) > amountStaked, "The participant has not enought AHT to spend");

        uint256 allowance = ahtToken.allowance(msg.sender, address(this));

        require(allowance >= amountStaked, "Check the token allowance");

        surveyDB.sendStake(surveyName, amountStaked, msg.sender, stakeStartDate);

        ahtToken.transferFrom(msg.sender, address(this), amountStaked);

        emit StakingPost(amountStaked, msg.sender);
    }

    /**
    * @notice Stop stake
    * @param surveyName Survey name
    * @param dateStop Date stop stake
    */
    function stopStake (bytes32 surveyName, uint dateStop) external {

        bool pay = surveyDB.isPayable(surveyName, msg.sender, dateStop);

        if (pay) {
            uint256 amountStaked = surveyDB.getAmountStaked(msg.sender);

            ahtToken.transfer(msg.sender, amountStaked);

            surveyDB.returnAmountStake(msg.sender);

            emit ReturnAmountStaked(msg.sender, address(this));
        }

        surveyDB.stopStake(surveyName, msg.sender, dateStop);

        emit StakingStopPost(surveyName, msg.sender, dateStop);
    }

    /**
    * @notice Send user reward
    * @param surveyName Survey name
    * @param participant Address participant
    * @param reward Reward amount
    * @param datePayout Date payout
    */
    function sendReward(bytes32 surveyName, address participant, uint256 reward, uint datePayout) 
        external onlyAdmin(1) {
        surveyDB.sendReward(surveyName, participant, reward, datePayout);

        emit SendReward(surveyName, participant, reward, datePayout, msg.sender);
    }

    /**
    * @notice Return the amount staked to some user that stopped the staking
    * @param sender Address sender
    */
    function returnAmountStaked(address sender) external onlyAdmin(1) {

        uint256 amountStaked = surveyDB.getAmountStaked(sender);

        ahtToken.transfer(msg.sender, amountStaked);

        surveyDB.returnAmountStake(sender);

        emit ReturnAmountStaked(sender, msg.sender);
    }

    /**
    * @notice Get survey data
    * @param surveyName Survey name
    * @return options Survey options
    * @return open Survey status
    * @return exists The survey exists
    * @return address Owner addres
    */
    function getSurvey (bytes32 surveyName) external view 
        returns (bytes32[] options, bool exists, uint256 rewardPool, uint endDate, address owner) {
        return surveyDB.getSurvey(surveyName);
    }

    /**
    * @notice Get total amount staked
    * @return amountStaked Total amount staked
    */
    function getTotalAmountStacked () external view returns (uint256 amountStaked) {
        return surveyDB.getTotalAmountStacked();
    }

    /**
    * @notice Get survey reward pool
    * @param surveyName Survey name
    * @return rewardPool Survey reward pool
    */
    function getSurveyRewardPool (bytes32 surveyName) external view returns (uint256 rewardPool) {
        return surveyDB.getSurveyRewardPool(surveyName);
    }

    /**
    * @notice Get survey staking history
    * @param surveyName Survey name
    * @return sender Senders address
    * @return payoutType Payout type by each sender in the same order than the senders
    * @return time Date payout by each sender in the same order than the senders
    */
    function getSurveyStakingHistory(bytes32 surveyName) external view 
        returns (address[] sender, uint[] payoutType, uint[] time) {
        return surveyDB.getSurveyStakingHistory(surveyName);
    }

    /**
    * @notice Get amount staked by user
    * @return amountStaked Amount staked
    */
    function getAmountStaked () external view returns (uint256 amountStaked) {
        return surveyDB.getAmountStaked(msg.sender);
    }

    /**
    * @notice Get staking info
    * @param surveyName Survey name
    * @return stakingActive Staking active status
    * @return stakingActiveDate Stake start date
    * @return amountStaked Amount staked
    * @return rewards Rewards
    */
    function getStakingInfo (bytes32 surveyName) external view 
        returns (bool stakingActive, uint stakingActiveDate, uint256 amountStaked, uint256 rewards) {
        return surveyDB.getStakingInfo(surveyName, msg.sender);
    }

    /**
    * @notice Get vote info from a specific survey
    * @return voted Flag to see if the participant voted
    * @return dateVoted Date voted
    */
    function getVoteInfo(bytes32 surveyName) external view returns (bool voted, uint dateVoted) {
        return surveyDB.getVoteInfo(surveyName, msg.sender);
    }

    /**
    * @notice Get voted options by specific survey and user
    * @param surveyName Survey name
    * @return votedOptions Voted options
    */
    function getVotedOptions (bytes32 surveyName) external view returns (bytes32[] votedOptions) {
        return surveyDB.getVotedOptions(surveyName, msg.sender);
    }

    /**
    * @notice Get survey results
    * @param surveyName Survey name
    * @return options_ Survey options
    * @return AHTVotesFor_ AHT Votes FOR by each option in the same order than the survey options
    * @return AHTVotesAgainst_ AHT Votes AGAINST by each option in the same order tahn the survey options
    */
    function getSurveyResult(bytes32 surveyName) external view returns (bytes32[], uint256[], uint256[]) {
        return surveyDB.getSurveyResult(surveyName);
    }

    /**
    * @notice Get reward info by survey
    * @param surveyName Survey name
    * @return participants Survey participants
    * @return stakingTime Staking time by each participant in the same order than the participants
    * @return reward Reward by each participant in the same order than the participants
    * @return rewarded Flag to see if the participant was rewarded in the same order than the participants
    * @return stakeStatus Flag to see if the participant has staking active in the same order than the participants
    */
    function getRewardInfo(bytes32 surveyName) external onlyAdmin(1) 
        view returns (address[], uint[], uint256[], bool[], bool[]) {
        return surveyDB.getRewardInfo(surveyName);
    }

    /**
    * @notice Get participants pending of withdraw the amount staked and if they might do it
    * @param surveyName Survey name
    * @param date Date
    * @return participants Participants pending of withdraw
    * @return pay Flag to see if the participant migh withdraw the amount staked
    */
    function getParticipantsPendingPayment(bytes32 surveyName, uint date) external onlyAdmin(1) 
        view returns (address[], bool[]) {
        return surveyDB.getParticipantsPendingPayment(surveyName, date);
    }
}

